setTimeout(function(){
    var cityNameDom = document.querySelector('.cityName');
    var map = new BMap.Map("container");
    var nowCity = new BMap.LocalCity();
    nowCity.get(bdGetPosition);
    function bdGetPosition(result) {
        var cityName = result.name; //当前的城市名
        cityNameDom.innerText = cityName;
        console.log(cityName);
    }
},1000);
//获取用户信息
setTimeout(function(){
    message()
},800)
function message(){
    var data = {
        ...JSON.parse(window.localStorage.getItem("login")),
        WEB:"LY"
    };
    $.ajax({
        type:'POST',
        url:'http://api.1yunsong.com/user/index/web_index',
        data:data,
        success:function(res){
            if(res.ret){
                res.data.userImg !== "" ? document.getElementById("userImg").src = res.data.userImg : "../img/headview.png";
                document.getElementById("userName").innerText = "欢迎登陆云送官网~," + res.data.username;
            }else{
                alert(res.forUser);
            }
        }
    });
}
//退出登陆
function exit(){
    alert("您将退出登陆...");
    window.localStorage.removeItem("login");
    location.reload()
}


